import React from 'react';
import { PropTypes } from 'prop-types';

class BubbleItemDetails extends React.Component {
    state = {
        loading: true,
        bubble: {},
    };

    async componentDidMount() {
        const bubbleId = this.props.match.params.bubbleItemId;
        const url = `http://localhost:3500/api/bubbles/${bubbleId}`;    
        const response = await fetch(url);
        const data = await response.json()
        this.setState({ bubble: data });
        this.setState({loading: false });
    };

    render() {
        const { id, name, description, price, image } = this.state;
        console.log(this.props);
        return (
            <div>
                {this.state.loading ? 
                    <div>loading</div> 
                    : <div className="card card-body bg-light bubble">
                    <h3 className="bubble-name text-primary">{name}</h3>
                    <div className="img">
                        <img src={ image } alt="cart item"/>
                    </div>
                    <div className ="price">
                        <h4>Price : { price }</h4>
                    </div>
                    <div className="description"><p>{ description }</p></div>
                </div>}
            </div>
        )
    }
};


BubbleItemDetails.propTypes = {
    bubble : PropTypes.shape ({
        id: PropTypes.number.isRequired,
        // We need to have the name of the product we want to sell
        name: PropTypes.string.isRequired,
        // Description of the product is reccomended but not nessecary
        description: PropTypes.string,
        // Image is must usefull but not a must
        image: PropTypes.string,
        // Price is a must, the customer must know how much our product costs
        price: PropTypes.number.isRequired,

    })
    // The ID is a must have

};

export default BubbleItemDetails;
