        const currentBundle = bundles[i];
        bundleGroup[currentBundle.id] ={
            ...currentBundle,
            bubbles: [],
        }

        for (let j = 0; j < currentBundle.items.length; j++){
            const num = currentBundle.items[j] -1;
            // currentBundle.bubbles.append(bubbles[num]);
            bundleGroup
        }

    }   

    return bundleGroup;
}


export const getBubbles = async() => {
    const result = await fetch(constants.BUBBLES);
    if(!result.ok) { return []; }
    return result.json;

}
